import { StatusBar } from 'expo-status-bar';
import React, { useState } from 'react';
import { StyleSheet, Text, View, TextInput, Button, FlatList} from 'react-native';

export default function App() {

  const [num1, setNum1] = useState('');
  const [num2, setNum2] = useState('');
  const [result, setResult] = useState('');

  const [text, setText] = useState('');
  const [data, setData] = useState([]);

  const buttonPressedSum = () => {
    setNum1('');
    setNum2('');
    setText('');
    var sum = num1 + num2;
    setText(num1 + ' + ' + num2 + ' = ' + sum);
    setData([...data, {key: text}]);
    console.log(text);
    setResult(sum);


  }

  const buttonPressedSub = () => {
    setNum1('');
    setNum2('');
    var sub = num1 - num2;
    setResult(sub);
  }

  return (
    <View style={styles.container}>
      <View style={styles.containers}>
        <Text style={styles.headertext}>Calculator with history</Text>
        <StatusBar style="auto" />
        <Text style={styles.text}>Result: {result}</Text>
        <TextInput
          placeholder={'first number'}
          style={styles.textinput}
          onChangeText={num1 => setNum1(parseInt(num1, 10))}
          value={num1}
          keyboardType='numeric'
        />
        <TextInput
          placeholder={'second number'}
          style={styles.textinput}
          onChangeText={num2 => setNum2(parseInt(num2, 10))}
          value={num2}
          keyboardType='numeric'
        />
      </View>
      <View style={styles.containers}>
        <View>
          <Button onPress={buttonPressedSum} title="+" />
        </View> 
        <View>       
          <Button onPress={buttonPressedSub} title="-" />
        </View>
      </View>
      <View style={styles.containers}>
  <Text style={styles.text}>History:</Text>
      <FlatList
        data={data}
        renderItem={({item}) => <Text>{item.key}</Text>}
      />
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    height: 100,
    paddingTop: 100,
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'space-around'
  },
  containers: {
    flex: 1,
    alignItems: 'center',
  },
  headertext: {
    fontSize: 25,
    color: 'steelblue',
    fontWeight: 'bold',
    marginBottom: 20,
  },
  text: {
    fontSize: 20,
  },
  textinput: {
    width: 200,
    backgroundColor: 'white',
    borderColor: 'blue',
    borderWidth: 1,
    borderRadius: 5,
    marginTop: 10,
    padding: 10,
  },
  button: {
    width: 50, 
    margin: 10,
  },
});
