import { StatusBar } from 'expo-status-bar';
import React, { useEffect, useState } from 'react';
import { Alert, Button, StyleSheet, Text, TextInput, View } from 'react-native';
//import { Picker } from '@react-native-community/picker';

export default function App() {

  const[numInput, setNumInput] = useState('');
  const [pickerValues, setPickerValues] = useState('');

  useEffect(() => getCurrencyRates(), [])

  const getCurrencyRates = () => {
    const url = 'http://data.fixer.io/api/latest?access_key=8da276568f820f42b9fde6f5a3644cb5'
    fetch(url)
    .then((response) => response.json())
    .then((reponseJson) => {
      console.log(responseJson)
    })
    .catch((Error) =>
      Alert.alert('Error', Error))
  }

  const convert = () => {
    const number = Number(numInput)
    console.log('numinput', numInput, 'number', number)
  }

  return (
    <View style={styles.container}>
      <Text style={styles.headertext}>Euro conventer</Text>
      <StatusBar style="auto"></StatusBar>
      <Text style={styles.text}>Result:</Text>
      <Button onPress={convert} title='Convert'/>
      <TextInput style={styles.textinput}
        value={numInput}
        onChangeText={numInput => setNumInput(numInput)}
        keyboardType='numeric'
      />
     {/*  <Picker>
        <Picker.Item />
      </Picker> */}

    </View>

  );
}

const styles = StyleSheet.create({
  container: {
    height: 100,
    paddingTop: 100,
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'space-around'
  },
  headertext: {
    fontSize: 25,
    color: 'steelblue',
    fontWeight: 'bold',
  },
  text: {
    fontSize: 20,
  },
  textinput: {
    width: 200,
    borderColor: 'steelblue',
    borderWidth: 2,
    borderRadius: 5,
    padding: 10,
    //marginTop: 10,
  },
});
