import { StatusBar } from 'expo-status-bar';
import React, { useState } from 'react';
import { StyleSheet, Text, View, TextInput, Button, Alert} from 'react-native';
import { Picker } from '@react-native-community/picker';

export default function App() {

  const [num, setNum] = useState('');
  const [selectedValue, setSelectedValue] = useState('');
 // const [data, setData] = useState([]);
  const data = [ 
    {'USD' : 4.45},
    {'GDH' : 1.56},
    {'JDN' : 3.45},
  ]

  const buttonPressed = () => {
    const number = Number(num);
    getCurrency()
    console.log('button pressed');
    console.log(number);
    console.log(selectedValue);
    console.log(data)
  }

  const getCurrency = () => {
    const url = 'http://data.fixer.io/api/latest?access_key=8da276568f820f42b9fde6f5a3644cb5'
    fetch(url)
    .then((response) => response.json())
    .then((responseJson) => {
      console.log(responseJson)
      setData(responseJson.rates);
      console.log(data)
    })
    .catch((error) => { 
      Alert.alert('Error' , error); 
    }); 
  }

  return (

    <View style={styles.container}>
       <View style={{flex: 0.3}}>
        <Text style={styles.headertext}>Euro converter</Text>
        <StatusBar style="auto" />
        <Text style={styles.text}>Result:</Text>
      </View>
      <View style={{flex:0.3}}>
        <TextInput
          style={styles.textinput}
          onChangeText={num => setNum(num)}
          value={num}
          keyboardType='number-pad'
        />
        </View>
        <View style={{flex:1}}>
        <Picker
          selectedValue={selectedValue}
          style={{ height: 50, width: 150, backgroundColor: 'blue' }}
          onValueChange={(itemValue, itemIndex) => setSelectedValue(itemValue)}
        > 
          {data.map((item, key) => {
            return(
              <Picker.Item label={item.name} key={item.name}/>
            )
          })}
          </Picker>
      </View>
      <View style={{flex: 1}}>
        <View style={styles.button}>
          <Button onPress={buttonPressed} title="convert" />
        </View> 
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    height: 100,
    paddingTop: 100,
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'space-around'
  },
  headertext: {
    fontSize: 25,
    color: 'steelblue',
    fontWeight: 'bold',
  },
  text: {
    fontSize: 20,
  },
  textinput: {
    width: 200,
    backgroundColor: 'white',
    borderColor: 'steelblue',
    borderWidth: 1,
    borderRadius: 5,
    marginTop: 10,
    padding: 10,
  },
  button: {
    width: 100, 
    margin: 5,
  },
});
